(function($){$.fn.extend({EXslider:function(){return this.each(function(){var obj=$(this);var totalWidth=obj.outerWidth();var totalHeight=parseInt(totalWidth*9/16);if(totalHeight<250)totalHeight=250;obj.height(totalHeight+75);obj.find('.slider-for').children().addClass('oneItem').height(totalHeight);obj.find('.slider-for .oneItem:first-child').addClass('active');var ztop=totalHeight/2-45;obj.find('.btn_left').css('top',ztop);obj.find('.btn_right').css('top',ztop);var totalItem=obj.find('.slider-nav li').size();if(totalItem>1)obj.find('.slider-nav').show();var widthItem=86;var ipp=Math.round(totalWidth/widthItem/2);obj.find('.slider-nav li').click(function(){obj.find('.slider-nav li.active').removeClass('active');$(this).addClass('active');var index=$(this).index();obj.find('.slider-for .active').removeClass('active');var e=obj.find('.slider-for .oneItem:eq('+index+')');e.addClass('active');var z=index-ipp;if(index>(totalItem-ipp))z=totalItem-(ipp*2)+1;else if(index<ipp)z=0;z=z*widthItem;if(z>(totalItem*widthItem)-totalWidth)z=(totalItem*widthItem)-totalWidth;obj.find('.slider-nav').animate({scrollLeft:z});return false;});obj.find('.btn_right').click(function(){var e=obj.find('.slider-for .active');e=e.next();obj.find('.slider-for .active').removeClass('active');if(e.length)e.addClass('active');else obj.find('.slider-for .oneItem:first-child').addClass('active');$(this).blur();return false;});obj.find('.btn_left').click(function(){var e=obj.find('.slider-for .active');e=e.prev();obj.find('.slider-for .active').removeClass('active');if(e.length)e.addClass('active');else obj.find('.slider-for .oneItem:last-child').addClass('active');$(this).blur();return false;});obj.swipe({swipeLeft:function(event,direction,distance,duration,fingerCount){obj.find('.btn_left').click();},swipeRight:function(event,direction,distance,duration,fingerCount){obj.find('.btn_right').click();}});});}});})(jQuery);